import React, { useState, useEffect } from 'react';
import { updateProfile, fetchProfile } from '../../services/userService';
import '../../styles/Profile.css';

const Profile = () => {
  const [role, setRole] = useState('student');
  const [profileData, setProfileData] = useState({});
  const [formData, setFormData] = useState({});
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    setLoading(true);
    setError('');
    fetchProfile(role)
      .then((data) => {
        setProfileData(data);
        setFormData(data);
      })
      .catch((err) => setError('Failed to load profile data.'))
      .finally(() => setLoading(false));
  }, [role]);

  const handleRoleChange = (newRole) => setRole(newRole);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await updateProfile(role, formData);
      alert('Profile updated successfully');
      setProfileData(formData); // Update local state with form data
    } catch (err) {
      alert('Error updating profile. Please try again.');
    }
  };

  return (
    <div className="profile-container">
      <h1>Your Profile</h1>
      <RoleSelector role={role} onRoleChange={handleRoleChange} />

      {loading ? (
        <p>Loading...</p>
      ) : error ? (
        <p className="error-message">{error}</p>
      ) : (
        <ProfileForm
          role={role}
          formData={formData}
          onChange={handleChange}
          onSubmit={handleSubmit}
        />
      )}

      <Sidebar />
    </div>
  );
};

// Role Selector Component
const RoleSelector = ({ role, onRoleChange }) => (
  <div className="role-selection">
    {['student', 'mentor', 'event_creator'].map((roleName) => (
      <button
        key={roleName}
        onClick={() => onRoleChange(roleName)}
        className={role === roleName ? 'active' : ''}
      >
        {roleName.charAt(0).toUpperCase() + roleName.slice(1)}
      </button>
    ))}
  </div>
);

// Profile Form Component
const ProfileForm = ({ role, formData, onChange, onSubmit }) => {
  const renderFormFields = () => {
    switch (role) {
      case 'student':
        return (
          <>
            <FormField
              label="Career Goal"
              name="careerGoal"
              type="text"
              value={formData.careerGoal || ''}
              onChange={onChange}
              placeholder="E.g., Engineer, Medical Doctor, etc."
            />
            <FormField
              label="High School / Homeschooled"
              name="highSchool"
              type="text"
              value={formData.highSchool || ''}
              onChange={onChange}
              placeholder="Enter your school name or 'Homeschooled'"
            />
            <FormField
              label="Do you have a computer?"
              name="hasComputer"
              type="checkbox"
              checked={formData.hasComputer || false}
              onChange={onChange}
            />
            <FormField
              label="Stable Internet?"
              name="hasStableInternet"
              type="checkbox"
              checked={formData.hasStableInternet || false}
              onChange={onChange}
            />
            <FormField
              label="Motivation Letter"
              name="motivationLetter"
              type="textarea"
              value={formData.motivationLetter || ''}
              onChange={onChange}
              placeholder="Write a short motivation letter (250-500 words)"
            />
          </>
        );
      case 'mentor':
        return (
          <>
            <FormField
              label="Field of Study / Occupation"
              name="fieldOfStudy"
              type="text"
              value={formData.fieldOfStudy || ''}
              onChange={onChange}
              placeholder="Enter your field of study or current occupation"
            />
            <FormField
              label="Career Goals"
              name="careerGoals"
              type="textarea"
              value={formData.careerGoals || ''}
              onChange={onChange}
              placeholder="Describe your career goals"
            />
            <FormField
              label="Projects"
              name="projects"
              type="textarea"
              value={formData.projects || ''}
              onChange={onChange}
              placeholder="List ongoing projects"
            />
            <FormField
              label="Experience"
              name="experience"
              type="textarea"
              value={formData.experience || ''}
              onChange={onChange}
              placeholder="Describe your experience"
            />
            <FormField
              label="Available for Live Sessions?"
              name="availableForLiveSessions"
              type="checkbox"
              checked={formData.availableForLiveSessions || false}
              onChange={onChange}
            />
            <FormField
              label="Available for Physical Sessions?"
              name="availableForPhysicalSessions"
              type="checkbox"
              checked={formData.availableForPhysicalSessions || false}
              onChange={onChange}
            />
          </>
        );
      case 'event_creator':
        return (
          <>
            <FormField
              label="Event Name"
              name="eventName"
              type="text"
              value={formData.eventName || ''}
              onChange={onChange}
              placeholder="Enter event name"
            />
            <FormField
              label="Event Category"
              name="eventCategory"
              type="text"
              value={formData.eventCategory || ''}
              onChange={onChange}
              placeholder="e.g., Conference, Webinar"
            />
            <FormField
              label="Location"
              name="eventLocation"
              type="text"
              value={formData.eventLocation || ''}
              onChange={onChange}
              placeholder="Enter location or 'Online'"
            />
            <FormField
              label="Expected Audience"
              name="expectedAudience"
              type="number"
              value={formData.expectedAudience || ''}
              onChange={onChange}
              placeholder="Enter expected number of attendees"
            />
            <FormField
              label="Event Agenda"
              name="eventAgenda"
              type="textarea"
              value={formData.eventAgenda || ''}
              onChange={onChange}
              placeholder="Describe the event agenda"
            />
          </>
        );
      default:
        return null;
    }
  };

  return (
    <form onSubmit={onSubmit} className="profile-form">
      {renderFormFields()}
      <button type="submit">Update Profile</button>
    </form>
  );
};

// Reusable FormField Component
const FormField = ({ label, name, type, value, onChange, placeholder, checked }) => (
  <div className="form-group">
    <label>{label}</label>
    {type === 'textarea' ? (
      <textarea
        name={name}
        value={value}
        onChange={onChange}
        placeholder={placeholder}
      ></textarea>
    ) : type === 'checkbox' ? (
      <input
        type="checkbox"
        name={name}
        checked={checked}
        onChange={(e) => onChange(e)}
      />
    ) : (
      <input
        type={type}
        name={name}
        value={value}
        onChange={onChange}
        placeholder={placeholder}
      />
    )}
  </div>
);

export default Profile;
